mapper.select:
SELECT
    t1.*,
    t2.department,
    t3.major,
    t4.cla
FROM
    student t1
    LEFT JOIN department t2 ON ( t1.department_id = t2.id )
    LEFT JOIN major t3 ON ( t1.major_id = t3.id )
    LEFT JOIN cla t4 ON ( t1.cla_id = t4.id )


      t1.id = #{id}



      AND t1.name LIKE #{pattern}


      AND DATE_FORMAT(t1.create_date,'%Y-%m-%d') = #{dateStr}


      AND t1.status = #{status}


      AND t1.department_id = #{departmentId}


      AND t1.major_id = #{majorId}


      AND t1.cla_id = #{claId}

SELECT t1.id, t1.NAME, t2.department, t1.department_id, t3.major, t1.major_id, t4.cla, t1.cla_id FROM student t1 LEFT JOIN department t2 ON ( t1.department_id = t2.id ) LEFT JOIN major t3 ON ( t1.major_id = t3.id ) LEFT JOIN cla t4 ON ( t1.cla_id = t4.id )
t1.id = #{id}
